clc; clear all;n = 6.02214e23      % Cte de AvogadroZa = +1               %% Carga Ion aZb = -1               %% Carga Ion bm = 1.74756                %% Cte de Madelung(Consultar Tabela)eletron = 1.6021e-19% Carga do elétronConstDie = 8.854e-12% Cte Dielétrica no vácuoRio = (102+181)             %% Raio iônico em picômetrosr = 34.5            % Cte de Repulsão%u = -((n*abs(Za*Zb)*m*(eletron^2))/(4*pi*ConstDie*Rio))*(1-(r/Rio))u1 = (n*abs(Za*Zb)*m*(eletron^2))u2 = (4*pi*ConstDie*Rio*10^-12)u3 = (1-(r/Rio))U = (-u1/u2)*u3UemKJ = U/1000